import React, { Component } from 'react';
import TOC from './components/TOC';
import Content from './components/Content';
import Subject from './components/Subject';
import './App.css';


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode:'welcome',
      subject:{
        title:'WEB', sub:'world wide web!'
      },
      //타이틀이 welcome일 때의 텍스트
      welcome:{title:'Welcome', desc:'Hello, React!!'},
      contents:[
        {id:1, title:'HTML', desc:'HTML is HyperText'},
        {id:2, title:'CSS', desc:'CSS is for design'},
        {id:3, title:'JavaScript', desc:'JS is for interactive'}
    ]
    }
  }

  //render() : 어떤 HTML을 그릴 것인지 결정하는 함수
  render() {

    console.log('App render');
    //mode의 값에 따라서 만들어지는 컴포넌트(render)의 결과가 바뀌도록 조건을 생성
    //<Content title = {_title}
  var _title, _desc = null;
  if (this.state.mode === 'welcome') {
    _title = this.state.welcome.title;
    _desc = this.state.welcome.desc;
  } else if (this.state.mode === 'read') {
    _title = this.state.contents[0].title;
    _desc = this.state.contents[0].desc;
  }
  return (
    <div className="App">
      {/* <Subject title={this.state.subject.title} sub={this.state.subject.sub}></Subject> */}

      <header>
        {/* HTML은 onclick으로 작성하지만 리액트는 onClick으로 작성,
            HTML은 문자열을 =""으로 작성하지만 리액트는 ={}으로 작성. */}
        {/* 이름이 없는 사용자 정의 함수는 a 링크를 클릭했을 때 실행된다. */}
        {/* 리액트는 함수의 첫번째 매개변수의 값으로 이벤트라는 객체를 주입한다. */}
            <h1><a href="/" onClick={function(e){
              console.log(e);
              debugger;
            }}>{this.state.subject.title}</a></h1>
            {this.state.subject.sub}
      </header>

      <TOC data={this.state.contents}></TOC>
      <Content title={_title} desc={_desc}></Content>
    </div>
  );
}
}

export default App;
